import { createReducer, on } from '@ngrx/store';
import * as FormsActions from './forms.actions';
const initialState = {
  list: [],
  loading: false
};
export const formsReducer = createReducer(initialState, on(FormsActions.loadForms, state => ({
  ...state,
  loading: true
})), on(FormsActions.loadFormsSuccess, (state, {
  forms
}) => ({
  ...state,
  list: forms,
  loading: false
})), on(FormsActions.saveFormSuccess, (state, {
  form
}) => {
  const exists = state.list.findIndex(f => f.id === form.id);
  if (exists === -1) {
    return {
      ...state,
      list: [...state.list, form]
    };
  }
  return {
    ...state,
    list: state.list.map(f => f.id === form.id ? form : f)
  };
}));
